CREATE TABLE Customers (
    CustomerId INT IDENTITY(1,1) PRIMARY KEY,
    CustomerName NVARCHAR(100) NOT NULL,
    CustomerSurname NVARCHAR(100) NOT NULL,
    CustomerEmail NVARCHAR(150) NOT NULL UNIQUE,
    CustomerPhone NVARCHAR(20) NULL,
    CustomerImageUrl NVARCHAR(255) NULL,
    CustomerCountry NVARCHAR(100) NULL,
    CustomerCity NVARCHAR(100) NULL,
    CustomerDistrict NVARCHAR(100) NULL,
    CustomerAddress NVARCHAR(255) NULL
);


GO

SET NOCOUNT ON;

-- Eğer tablo boşaltılacaksa aşağıdaki satırı açabilirsin:
-- TRUNCATE TABLE Customers;

DECLARE @i INT = 1;
DECLARE @TotalRows INT = 1000;

-- Değişkenler
DECLARE @CountryIndex INT;
DECLARE @RandIndex INT; -- Rastgele sayıları sabitlemek için
DECLARE @SelectedCountry NVARCHAR(100);
DECLARE @SelectedCity NVARCHAR(100);
DECLARE @FirstName NVARCHAR(100);
DECLARE @LastName NVARCHAR(100);
DECLARE @Email NVARCHAR(150);
DECLARE @Phone NVARCHAR(20);
DECLARE @PhoneCode NVARCHAR(5);
DECLARE @Address NVARCHAR(255);
DECLARE @ImageUrl NVARCHAR(255);

BEGIN TRY
    BEGIN TRANSACTION;

    WHILE @i <= @TotalRows
    BEGIN
        -- Her döngüde değişkenleri temizle
        SET @SelectedCountry = NULL;
        SET @SelectedCity = NULL;
        SET @FirstName = NULL;
        SET @LastName = NULL;

        -- 1 ile 5 arasında rastgele ülke seçimi
        SET @CountryIndex = FLOOR(RAND() * 5) + 1;

        -------------------------------------------------------
        -- ÜLKE VE İSİM MANTIĞI
        -------------------------------------------------------
        IF @CountryIndex = 1 -- ALMANYA
        BEGIN
            SET @SelectedCountry = 'Germany';
            SET @PhoneCode = '+49';
            
            SET @RandIndex = FLOOR(RAND() * 5) + 1;
            SET @SelectedCity = CHOOSE(@RandIndex, 'Berlin', 'Munich', 'Hamburg', 'Frankfurt', 'Cologne');
            
            SET @RandIndex = FLOOR(RAND() * 10) + 1;
            SET @FirstName = CHOOSE(@RandIndex, 'Hans', 'Klaus', 'Julia', 'Stefan', 'Claudia', 'Thomas', 'Sabine', 'Michael', 'Andreas', 'Petra');
            
            SET @RandIndex = FLOOR(RAND() * 10) + 1;
            SET @LastName = CHOOSE(@RandIndex, 'Mueller', 'Schmidt', 'Schneider', 'Fischer', 'Weber', 'Meyer', 'Wagner', 'Becker', 'Schulz', 'Hoffmann');
        END
        ELSE IF @CountryIndex = 2 -- FRANSA
        BEGIN
            SET @SelectedCountry = 'France';
            SET @PhoneCode = '+33';
            
            SET @RandIndex = FLOOR(RAND() * 5) + 1;
            SET @SelectedCity = CHOOSE(@RandIndex, 'Paris', 'Lyon', 'Marseille', 'Toulouse', 'Nice');
            
            SET @RandIndex = FLOOR(RAND() * 10) + 1;
            SET @FirstName = CHOOSE(@RandIndex, 'Jean', 'Marie', 'Pierre', 'Sophie', 'Michel', 'Camille', 'Lucas', 'Lea', 'Antoine', 'Chloe');
            
            SET @RandIndex = FLOOR(RAND() * 10) + 1;
            SET @LastName = CHOOSE(@RandIndex, 'Martin', 'Bernard', 'Dubois', 'Thomas', 'Robert', 'Richard', 'Petit', 'Durand', 'Leroy', 'Moreau');
        END
        ELSE IF @CountryIndex = 3 -- İSPANYA
        BEGIN
            SET @SelectedCountry = 'Spain';
            SET @PhoneCode = '+34';
            
            SET @RandIndex = FLOOR(RAND() * 5) + 1;
            SET @SelectedCity = CHOOSE(@RandIndex, 'Madrid', 'Barcelona', 'Valencia', 'Seville', 'Bilbao');
            
            SET @RandIndex = FLOOR(RAND() * 10) + 1;
            SET @FirstName = CHOOSE(@RandIndex, 'Antonio', 'Carmen', 'Jose', 'Maria', 'Manuel', 'Ana', 'David', 'Laura', 'Javier', 'Isabel');
            
            SET @RandIndex = FLOOR(RAND() * 10) + 1;
            SET @LastName = CHOOSE(@RandIndex, 'Garcia', 'Rodriguez', 'Gonzalez', 'Fernandez', 'Lopez', 'Martinez', 'Sanchez', 'Perez', 'Gomez', 'Martin');
        END
        ELSE IF @CountryIndex = 4 -- İTALYA
        BEGIN
            SET @SelectedCountry = 'Italy';
            SET @PhoneCode = '+39';
            
            SET @RandIndex = FLOOR(RAND() * 5) + 1;
            SET @SelectedCity = CHOOSE(@RandIndex, 'Rome', 'Milan', 'Naples', 'Turin', 'Florence');
            
            SET @RandIndex = FLOOR(RAND() * 10) + 1;
            SET @FirstName = CHOOSE(@RandIndex, 'Marco', 'Sofia', 'Alessandro', 'Giulia', 'Giuseppe', 'Francesca', 'Luca', 'Anna', 'Matteo', 'Chiara');
            
            SET @RandIndex = FLOOR(RAND() * 10) + 1;
            SET @LastName = CHOOSE(@RandIndex, 'Rossi', 'Russo', 'Ferrari', 'Esposito', 'Bianchi', 'Romano', 'Colombo', 'Ricci', 'Marino', 'Greco');
        END
        ELSE -- HOLLANDA (Varsayılan)
        BEGIN
            SET @SelectedCountry = 'Netherlands';
            SET @PhoneCode = '+31';
            
            SET @RandIndex = FLOOR(RAND() * 5) + 1;
            SET @SelectedCity = CHOOSE(@RandIndex, 'Amsterdam', 'Rotterdam', 'The Hague', 'Utrecht', 'Eindhoven');
            
            SET @RandIndex = FLOOR(RAND() * 10) + 1;
            SET @FirstName = CHOOSE(@RandIndex, 'Jan', 'Johanna', 'Willem', 'Sanne', 'Johannes', 'Emma', 'Pieter', 'Eva', 'Dennis', 'Lisa');
            
            SET @RandIndex = FLOOR(RAND() * 10) + 1;
            SET @LastName = CHOOSE(@RandIndex, 'De Jong', 'Jansen', 'De Vries', 'Van den Berg', 'Van Dijk', 'Bakker', 'Janssen', 'Visser', 'Smit', 'Meijer');
        END

        -------------------------------------------------------
        -- GÜVENLİK KONTROLÜ (NULL ÖNLEME)
        -------------------------------------------------------
        IF @FirstName IS NULL SET @FirstName = 'User' + CAST(@i AS NVARCHAR);
        IF @LastName IS NULL SET @LastName = 'Eu';
        IF @SelectedCity IS NULL SET @SelectedCity = 'City';

        -------------------------------------------------------
        -- VERİ OLUŞTURMA
        -------------------------------------------------------
        
        -- Email Unique Garantisi: İsim + Soyisim + DöngüSayısı (@i)
        SET @Email = LOWER(@FirstName) + '.' + LOWER(REPLACE(@LastName, ' ', '')) + '_' + CAST(@i AS NVARCHAR(10)) + '@mail.eu';

        -- Telefon
        SET @Phone = @PhoneCode + ' ' + CAST(FLOOR(RAND() * (999-100)+100) AS NVARCHAR(10)) + ' ' + CAST(FLOOR(RAND() * (9999-1000)+1000) AS NVARCHAR(10));

        -- Adres
        SET @Address = CAST(FLOOR(RAND() * 200) + 1 AS NVARCHAR(10)) + ' ' + @SelectedCity + ' St., Apt ' + CAST(FLOOR(RAND() * 50) + 1 AS NVARCHAR(10));

        -- RESİM URL (İstediğin format: picsum.photos/seed/{id}/200)
        -- Seed kullanıyoruz ki aynı ID için hep aynı resim gelsin, ID olarak döngü sayısını (@i) veriyoruz.
        SET @ImageUrl = 'https://picsum.photos/seed/' + CAST(@i AS NVARCHAR(10)) + '/200';

        -------------------------------------------------------
        -- INSERT
        -------------------------------------------------------
            CustomerName, 
            CustomerSurname, 
            CustomerEmail, 
            CustomerPhone, 
            CustomerImageUrl, 
            CustomerCountry, 
            CustomerCity, 
            CustomerAddress
        )
        VALUES (
            @FirstName,
            @LastName,
            @Email,
            @Phone,
            @ImageUrl,
            @SelectedCountry,
            @SelectedCity,
            @Address
        );

        SET @i = @i + 1;
    END

    COMMIT TRANSACTION;
    PRINT 'İşlem Başarılı: 1000 Kayıt (Picsum Resimler Dahil) Eklendi.';

END TRY
BEGIN CATCH
    IF @@TRANCOUNT > 0
        ROLLBACK TRANSACTION;
    
    PRINT 'HATA OLUŞTU!';
    PRINT 'Hata Mesajı: ' + ERROR_MESSAGE();
END CATCH;

SET NOCOUNT OFF;

GO